  @Public()
  @Get('api/health')
  @HealthCheck()
  check() {
    return this.healthCheckService.check([
      async () => this.prismaIndicator.pingCheck('database', { 
        timeout: 3000,
        prismaService: this.prismaService 
      }),
      // Add memory health check
      () => ({ memory: { status: 'up', details: process.memoryUsage() } }),
    ]);
  }
}
